
DROP TABLE IF EXISTS "categorie";
CREATE TABLE "categorie" (
  "pkCat" SERIAL PRIMARY KEY,
  "Ordine" int NOT NULL DEFAULT 0,
  "Ordine_Query" int NOT NULL DEFAULT 0,
  "Categoria" text NOT NULL,
  "Sottocategoria" text
);

DROP TABLE IF EXISTS "ingredienti";
CREATE TABLE "ingredienti" (
  "pkIngrediente" SERIAL PRIMARY KEY,
  "Ingrediente" text NOT NULL
 );

DROP TABLE IF EXISTS "misure";
CREATE TABLE "misure" (
  "pkMisura" SERIAL PRIMARY KEY,
  "Misura" text NOT NULL
);

DROP TABLE IF EXISTS "ricette";
CREATE TABLE "ricette" (
  "pkRicetta" SERIAL PRIMARY KEY,
  "Titolo" text NOT NULL,
  "stampata" INT NOT NULL DEFAULT 0,
  "Porzioni" text,
  "N_porzioni" decimal(10,1) DEFAULT NULL,
  "Tempo_preparazione" INTERVAL DEFAULT NULL,
  "Tempo_cottura" INTERVAL DEFAULT NULL,
  "Tempo_agg" INTERVAL DEFAULT NULL,
  "fkCat" int DEFAULT NULL REFERENCES "categorie"("pkCat") ON DELETE SET NULL ON UPDATE CASCADE,
  "Etnica" text,
  "Data" date NOT NULL,
  "Immagine" text,
  "Esecuzione" text NOT NULL,
  "Diff" int DEFAULT NULL,
  "Calorie" int DEFAULT NULL,
  "Commenti" int NOT NULL DEFAULT 0,
  "Cottura" text,
  "Autore" text NOT NULL
);

DROP TABLE IF EXISTS "commenti";
CREATE TABLE "commenti" (
  "pkCommento" SERIAL PRIMARY KEY,
  "fkRicetta" int NOT NULL REFERENCES "ricette"("pkRicetta") ON DELETE CASCADE ON UPDATE CASCADE,
  "Data" date NOT NULL,
  "Commento" text NOT NULL,
  "Autore" text NOT NULL
);

DROP TABLE IF EXISTS "ingredienti_ricette";
CREATE TABLE "ingredienti_ricette" (
  "pkRow" SERIAL PRIMARY KEY,
  "fkRicetta" int NOT NULL REFERENCES "ricette"("pkRicetta") ON DELETE CASCADE ON UPDATE CASCADE,
  "fkIngrediente" int NOT NULL REFERENCES "ingredienti"("pkIngrediente") ON DELETE CASCADE ON UPDATE CASCADE,
  "Quant" decimal(5,1) DEFAULT NULL,
  "fkMisura" int DEFAULT NULL REFERENCES "misure"("pkMisura") ON DELETE SET NULL ON UPDATE CASCADE,
  "Dettagli" text
);

DROP TABLE IF EXISTS "link_ricette";
CREATE TABLE "link_ricette" (
  "pkLinkr" SERIAL PRIMARY KEY,
  "fkric1" int NOT NULL REFERENCES "ricette"("pkRicetta") ON DELETE CASCADE ON UPDATE CASCADE,
  "fkric2" int NOT NULL REFERENCES "ricette"("pkRicetta") ON DELETE CASCADE ON UPDATE CASCADE,
  "Doppio" int NOT NULL DEFAULT 0
);

